import '@picocss/pico';
import React, { useState, useEffect } from 'react';
import './styles/App.css'; // CSS 파일 임포트
import 'bootstrap/dist/css/bootstrap.min.css';
import Form from 'react-bootstrap/Form';
import PdfViewer from './pdfViewers.js';
import { sendPostRequest, sendPostRequest1, sendPostRequest2, sendPostRequest3 } from './request.js';

function App() {

  const [address, setAddress] = useState('');
  const [addrObj, setAddrObj] = useState(null);
  const [addrkey, setAddrkey] = useState("");

  const [dongObj, setDongObj] = useState(null);
  const [dongkey, setDongkey] = useState("");

  const [hoObj, setHoObj] = useState(null);
  const [hokey, setHokey] = useState(null);

  const [pdfHex, setpdfHex] = useState("");

  const [showSearch, setShowSearch] = useState(true);
  const [searchHidden, setSearchHidden] = useState(false);
  const [showAddr, setShowAddr] = useState(true);
  const [addrHidden, setAddrHidden] = useState(false);
  const [showDong, setShowDong] = useState(true);
  const [dongHidden, setDongHidden] = useState(false);
  const [showHo, setShowHo] = useState(true);
  const [hoHidden, setHoHidden] = useState(false);

  useEffect(() => {
    if (!showSearch) {
      const timer = setTimeout(() => setSearchHidden(true), 500);
      return () => clearTimeout(timer);
    }
  }, [showSearch]);

  useEffect(() => {
    if (!showAddr) {
      const timer = setTimeout(() => setAddrHidden(true), 500);
      return () => clearTimeout(timer);
    }
  }, [showAddr]);

  useEffect(() => {
    if (!showDong) {
      const timer = setTimeout(() => setDongHidden(true), 500);
      return () => clearTimeout(timer);
    }
  }, [showDong]);

  useEffect(() => {
    if (!showHo) {
      const timer = setTimeout(() => setHoHidden(true), 500);
      return () => clearTimeout(timer);
    }
  }, [showHo]);

  const handleAddressSearch = () => {
    console.log(address);
    setShowSearch(false);

    sendPostRequest(address).then(() => {
      const addrData = localStorage.getItem('addrObj');
      if (addrData) {
        setAddrObj(JSON.parse(addrData));
      }
    });
  };

  const handleAddrSearch = (addrkey) => {
    setShowAddr(false);

    sendPostRequest1(addrkey).then(() => {
      const dongData = localStorage.getItem('dongObj');
      if (dongData) {
        setDongObj(JSON.parse(dongData));
      }
    });
  };

  const handleDongSearch = (dongkey) => {
    setShowDong(false);

    sendPostRequest2(dongkey).then(() => {
      const hoData = localStorage.getItem('hoObj');
      if (hoData) {
        setHoObj(JSON.parse(hoData));
      }
    });
  };

  const handleHoSearch = (hokey) => {
    setShowHo(false);

    sendPostRequest3(hokey).then(() => {
      const pdfHex = localStorage.getItem('pdfHex');
      setpdfHex(pdfHex);
    });
  };

  return (
    <div className="background-image" style={{ backgroundImage: "url('/Apart.webp')" }}>
      <div className="overlay">
        <nav className="container-fluid">
          <ul>
            <li><strong>부동산 검색 포털</strong></li>
          </ul>
          <ul>
            <li><a href="#">홈</a></li>
            <li><a href="#">문의하기</a></li>
            <li><a href="#" role="button">로그인</a></li>
          </ul>
        </nav>
        <main className="container">
          <section>
            <hgroup>
              <h2>아파트 및 토지 검색</h2>
              <h3>원하는 위치와 조건을 검색해 보세요.</h3>
            </hgroup>
            <p>아래 검색 창에 원하는 조건을 입력하고 검색 버튼을 클릭하세요.</p>
            {!searchHidden && <주소입력창 setAddress={setAddress} handleAddressSearch={handleAddressSearch} showSearch={showSearch} />}
            {addrObj && !addrHidden && <주소선택창 addrObj={addrObj} addrkey={addrkey} setAddrkey={setAddrkey} setDongObj={setDongObj} showAddr={showAddr} handleAddrSearch={handleAddrSearch} />}
            {dongObj && !dongHidden && <동선택창 dongObj={dongObj} dongkey={dongkey} setDongkey={setDongkey} setHoObj={setHoObj} showDong={showDong} handleDongSearch={handleDongSearch} />}
            {hoObj && !hoHidden && <호선택창 hoObj={hoObj} hokey={hokey} setHokey={setHokey} setpdfHex={setpdfHex} showHo={showHo} handleHoSearch={handleHoSearch} />}
            <h3>문서 보기</h3>
            <p>관련 문서를 PDF 형태로 확인할 수 있습니다.</p>
            {pdfHex && <PdfViewer hexString={pdfHex} />}
          </section>
        </main>
      </div>
      <footer className="container">
        <small><a href="#">개인정보처리방침</a> • <a href="#">이용약관</a></small>
      </footer>
    </div>
  );
}

function 주소입력창({ setAddress, handleAddressSearch, showSearch }) {
  return (
    <div className={`search-container ${!showSearch ? "hide-search" : ""}`}>
      <form action="#" method="get" onSubmit={(e) => e.preventDefault()}>
        <input type="text" id="search" name="search" placeholder="주소를 입력하고 '주소검색' 버튼을 누르세요." aria-label="검색어" required
          onChange={(e) => setAddress(e.target.value)} 
        />
        <button type="button" onClick={handleAddressSearch}>검색</button>
      </form>
    </div>
  );
}

function 주소선택창({ addrObj, addrkey, setAddrkey, setDongObj, showAddr, handleAddrSearch }) {

  const handleChange = (event) => {
    console.log(event.target.value);
    setAddrkey(event.target.value); // 선택된 항목의 value로 addrkey 상태 업데이트
  };

  return (
    <div className={`addr-container ${!showAddr ? "hide-addr" : ""}`}>
      <Form.Select aria-label="Default select example" style={{ width: '60%', margin: '0 auto' }}
        onChange={handleChange}>
        <option value="">원하는주소를 선택하세요</option>
        {addrObj.hits.hits.map((item, index) => (
          <option key={index} value={item._source.mgmUpperBldrgstPk}>{item._source.jibunAddr}</option>
        ))}
      </Form.Select>
      {addrkey && <button type="submit" onClick={() => handleAddrSearch(addrkey)}>동검색</button>}
    </div>
  );
}

